mapper.update:
insert into T_EBOOK_SNAPSHOT(ebook_id, date, view_count, vote_count, view_increase, vote_increase) ( select id, current_date, 0, 0, 0, 0 from T_EBOOK t1 where not exists ( select 1 from T_EBOOK_SNAPSHOT t2 where t2.date = current_date and t2.ebook_id = t1.id ) );
update T_EBOOK_SNAPSHOT t1 set view_count = t2.view_count, vote_count = t2.vote_count from T_EBOOK t2 where t1."date" = current_date and t1.ebook_id = t2.id;
update T_EBOOK_SNAPSHOT t1 set view_increase = (t1.view_count - coalesce(t2.view_count, 0)), vote_increase = (t1.vote_count - coalesce(t2.vote_count, 0)) from ( select t3.ebook_id, t4.view_count, t4.vote_count from T_EBOOK_SNAPSHOT t3 left join ( select ebook_id, view_count, vote_count from T_EBOOK_SNAPSHOT where date = current_date - 1 ) t4 on t3.ebook_id = t4.ebook_id where t3."date" = current_date ) t2 where t1.ebook_id = t2.ebook_id and t1."date" = current_date;
